import React, { useState, useEffect } from "react";
import axios from 'axios';
import Table from 'react-bootstrap/Table';
import MaterialTable from 'material-table';
import StudentTableRow from './StudentTableRow';


export default function StudentList(){

  const [columns, setColumns] = useState({
    data: [
      { title: 'Name', field: 'name' },
      { title: 'E-mail', field: 'email' },
      { title: 'Roll-Nº', field: 'rollno', type: 'numeric' }      
    ]
  });

  const [student, setStudent] = useState({
    data: []
  });

  useEffect(() => {
    axios.get('http://localhost:4000/students/')
      .then(res => {
        setStudent(res);
      })
      .catch((error) => {
        console.log(error);
      })
  }, []);

  return (
    <div>
      <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons"></link>
      <MaterialTable
        title="Editable Example"
        columns={columns.data}
        data={student.data}
        editable={{
          onRowAdd: newData =>
            new Promise(resolve => {
              setTimeout(() => {
                resolve();
                setStudent(prevState => {
                  const data = [...prevState.data];
                  data.push(newData);
                  axios.post('http://localhost:4000/create-student/', newData)
                    .then(res => {
                      console.log(res.data);
                    })
                    .catch((error) => {
                      console.log(error);
                    })
                  return { ...prevState, data };
                });
              }, 600);
            }),
          onRowUpdate: (newData, oldData) =>
            new Promise(resolve => {
              setTimeout(() => {
                resolve();
                if (oldData) {
                  setStudent(prevState => {
                    const data = [...prevState.data];
                    axios.put('http://localhost:4000/update-student/' + oldData._id, newData)
                      .then(res => {
                        console.log(res.data);
                      })
                      .catch((error) => {
                        console.log(error);
                      })
                    data[data.indexOf(oldData)] = newData;
                    return { ...prevState, data };
                  });
                }
              }, 600);
            }),
          onRowDelete: oldData =>
            new Promise(resolve => {
              setTimeout(() => {
                resolve();
                setStudent(prevState => {
                  const data = [...prevState.data];
                  axios.delete('http://localhost:4000/delete-student/' + oldData._id + '/')
                    .then(res => {
                      console.log(res.data);
                    })
                    .catch((error) => {
                      console.log(error);
                    })
                  data.splice(data.indexOf(oldData), 1);
                  return { ...prevState, data };
                });
              }, 600);
            }),
        }}
      />
      </div>
  );
}
